        public string ProcessArticle(string ArticleText, string ArticleTitle, int wikiNamespace, out string Summary, out bool Skip)   // line 17
        {
            Skip = false;
            Summary = "";
// Start Unofficial name match
Match approxNameMatch = Regex.Match(ArticleText, @"'*The(?: given)? (.+?) names?( given| for this card)? (is|are)( an)? (approximations?|not official)\.?'*\s*");
string text;
if (approxNameMatch.Success)
{
  ArticleText = ArticleText.Replace(approxNameMatch.Groups[0].Value, "");
  text = "{{Unofficial name|" + approxNameMatch.Groups[1].Value.Replace("[[Card Names:" + ArticleTitle + "|", "").Replace("]]", "").Replace(", and ", "|").Replace(", ", "|").Replace(" and ", "|") + "}}\r\n";
  ArticleText = text + ArticleText;
  Summary += "deploy {{Unofficial name}}";
}
// End Unofficial name match
// Start About/For match
Match aboutMatch = Regex.Match(ArticleText, @"\:?'*(?:This (?:article|page) is about (.*?)[.,;] )?[Ff]or (.*?),? see\:? \[\[(.*?)\]\]\.?'*\s*");
string Smry = "";
if (aboutMatch.Success)
{
  ArticleText = ArticleText.Replace(aboutMatch.Groups[0].Value, "");
  if (aboutMatch.Groups[1].Value != "")
  {
    text = "{{About|" + aboutMatch.Groups[1].Value;
    Smry = "{{About}}";
  }
  else
  {
    text = "{{For";
    Smry = "{{For}}";
  }
  text += "|" + aboutMatch.Groups[2].Value + "|" + aboutMatch.Groups[3].Value + "}}\r\n";
  ArticleText = text + ArticleText;
  if (Summary != "")
  {
    Summary += ", ";
  }
  Summary += "deploy " + Smry;
}
// Start {{Unofficial name}}/{{Unofficial lore}} match
Match unofficialNames = Regex.Match(ArticleText, @"\{\{Unofficial name\|([^\}]*)\}\}");
Match unofficialLores = Regex.Match(ArticleText, @"\{\{Unofficial lore\|([^\}]*)\}\}");
string newText;
if (unofficialNames.Success)
{
  newText = ArticleText.Replace(unofficialNames.Groups[1].Value, unofficialNames.Groups[1].Value.Replace("||", "|").Replace("|", ", "));
  if (newText != ArticleText)
  {
    ArticleText = newText;
  }
}
if (unofficialLores.Success)
{
  newText = ArticleText.Replace(unofficialLores.Groups[1].Value, unofficialLores.Groups[1].Value.Replace("||", "|").Replace("|", ", "));
  if (newText != ArticleText)
  {
    ArticleText = newText;
  }
}
  
  
            return ArticleText;
        }
